import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./QAList.css";

interface QAOverview {
    id: number;
    title: string;
}

interface QAStatus extends QAOverview {
    is_solved: boolean;
}

const QAList: React.FC = () => {
    const [qaList, setQaList] = useState<QAStatus[]>([]);
    const uid = localStorage.getItem("uid") || "guest";

    useEffect(() => {
        // ÂÖ®„ÇØ„Ç§„Ç∫‰∏ÄË¶ßÂèñÂæó
        axios.get("http://localhost:8000/qainfo")
            .then(async (response) => {
                const list: QAOverview[] = response.data;

                // ÂêÑ„ÇØ„Ç§„Ç∫„Å´„Å§„ÅÑ„Å¶ is_solved „ÇíÂïè„ÅÑÂêà„Çè„Åõ„Å¶‰ªòÂä†
                const updatedList: QAStatus[] = await Promise.all(
                    list.map(async (qa) => {
                        try {
                            const res = await axios.get(`http://localhost:8000/qaresult/${qa.id}`, {
                                params: { u_id: uid }
                            });
                            return { ...qa, is_solved: res.data.is_solved === 1 };
                        } catch (err) {
                            console.error(`Ëß£Á≠îÁä∂Ê≥Å„ÅÆÂèñÂæó„Å´Â§±Êïó: id=${qa.id}`, err);
                            return { ...qa, is_solved: false };
                        }
                    })
                );

                setQaList(updatedList);
            })
            .catch((error) => {
                console.error("„Éá„Éº„Çø„ÅÆÂèñÂæó„Å´Â§±Êïó„Åó„Åæ„Åó„Åü", error);
            });
    });

    return (
        <div className="qa-container">
            <h1 className="qa-title">QA‰∏ÄË¶ß</h1>
            <p className="qa-description">‰ª•‰∏ã„ÅØÁôªÈå≤„Åï„Çå„Å¶„ÅÑ„ÇãQAÈ†ÖÁõÆ„ÅÆ‰∏ÄË¶ß„Åß„Åô„ÄÇ„Ç´„Éº„Éâ„Çí„ÇØ„É™„ÉÉ„ÇØ„Åô„Çã„Å®Ë©≥Á¥∞„Éö„Éº„Ç∏„ÅåË°®Á§∫„Åï„Çå„Åæ„Åô„ÄÇ</p>
            <div className="qa-card-container">
                {qaList.map((qa) => (
                    qa.is_solved ? (
                        <div key={qa.id} className="qa-card solved">
                            <h2 className="qa-card-title">{qa.title}</h2>
                            <p className="qa-status">‚úÖ Ëß£Á≠îÊ∏à„Åø</p>
                        </div>
                    ) : (
                        <Link
                            to={`/qa/${qa.id}/solve`}
                            key={qa.id}
                            className="qa-card-link"
                            style={{ textDecoration: 'none', color: 'inherit' }}
                        >
                            <div className="qa-card">
                                <h2 className="qa-card-title">{qa.title}</h2>
                                <p className="qa-status">üìù Ëß£Á≠î„Åô„Çã</p>
                            </div>
                        </Link>
                    )
                ))}
            </div>
        </div>
    );
};

export default QAList;
