import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./QASolve.css";

interface QAItem {
    id: number;
    question: string;
    options: string[];
    answer: string; // 正答（送信用には使わない）
    mode: "multiple-choice" | "multiple-select" | "記述式問題";
}

const QASolve: React.FC = () => {
    const { id } = useParams();
    const [qa, setQa] = useState<QAItem | null>(null);
    const [selectedOption, setSelectedOption] = useState<string>("");
    const [textAnswer, setTextAnswer] = useState<string>("");
    const [submitted, setSubmitted] = useState(false);

    useEffect(() => {
        axios.get(`http://localhost:8000/qadetail/${id}`)
            .then(response => {
                setQa(response.data);
            })
            .catch(error => {
                console.error("データ取得に失敗しました", error);
            });
    }, [id]);

    const handleSubmit = () => {
        const payload = {
            qa_id: id,
            user_answer: qa?.mode === "記述式問題" ? textAnswer : selectedOption
        };

        axios.post("http://localhost:8000/submit", payload)
            .then(() => {
                alert("回答が送信されました");
                setSubmitted(true);
            })
            .catch(error => {
                console.error("回答送信に失敗しました", error);
            });
    };

    if (!qa) return <div>読み込み中...</div>;
    if (submitted) return <div>回答ありがとうございました。</div>;

    return (
        <div className="solve-container">
            <h2 className="solve-title">問題を解く</h2>
            <p className="solve-question">{qa.question}</p>

            {qa.mode === "multiple-choice" && (
                <div className="solve-options">
                    {qa.options.map((opt, index) => (
                        <label key={index} className="solve-option">
                            <input
                                type="radio"
                                name="option"
                                value={opt}
                                checked={selectedOption === opt}
                                onChange={(e) => setSelectedOption(e.target.value)}
                            />
                            {opt}
                        </label>
                    ))}
                </div>
            )}

            {qa.mode === "記述式問題" && (
                <textarea
                    className="solve-textarea"
                    value={textAnswer}
                    onChange={(e) => setTextAnswer(e.target.value)}
                    placeholder="ここに回答を入力してください"
                />
            )}

            {/* multiple-select の場合のUI追加も可能 */}

            <button className="solve-submit-button" onClick={handleSubmit} disabled={submitted}>
                回答する
            </button>
        </div>
    );
};

export default QASolve;
