import React from "react";
import { useParams } from "react-router-dom";
import QAEdit from "./QAEdit";

export interface QAItem {
    id: number;
    qa_id: number;
    question: string;
    options: string[];
    answer: string;
    mode: string;
    created_at: string;
    class: string;
    title: string;
}

export const mockQAData: Record<string, QAItem[]> = {
    "101": [
        {
            qa_id: 1,
            id: 101,
            question: "What is the capital of Japan?",
            options: ["Tokyo", "Osaka", "Kyoto"],
            answer: "Tokyo",
            mode: "multiple-choice",
            created_at: "2022-12-25",
            class: "地理",
            title: "日本の地理問題"
        },
        {
            qa_id: 2,
            qa_id: 101,
            question: "Which is the largest island in Japan?",
            options: ["Hokkaido", "Honshu", "Kyushu"],
            answer: "Honshu",
            mode: "multiple-choice",
            created_at: "2022-12-25",
            class: "地理",
            title: "日本の地理問題"
        },
        {
            qa_id: 3,
            qa_id: 101,
            question: "What sea lies between Japan and Korea?",
            options: ["Sea of Japan", "Pacific Ocean", "East China Sea"],
            answer: "Sea of Japan",
            mode: "multiple-choice",
            created_at: "2022-12-25",
            class: "地理",
            title: "日本の地理問題"
        },
        {
            id: 4,
            qa_id: 101,
            question: "Which region is Tokyo located in?",
            options: ["Kanto", "Kansai", "Tohoku"],
            answer: "Kanto",
            mode: "multiple-choice",
            created_at: "2022-12-25",
            class: "地理",
            title: "日本の地理問題"
        },
        {
            id: 5,
            qa_id: 101,
            question: "Which mountain is the highest in Japan?",
            options: ["Mount Fuji", "Mount Kita", "Mount Ontake"],
            answer: "Mount Fuji",
            mode: "multiple-choice",
            created_at: "2022-12-25",
            class: "地理",
            title: "日本の地理問題"
        }
    ],
    "102": [
        {
            id: 1,
            qa_id: 102,
            question: "What is gravity?",
            options: [],
            answer: "A force that attracts two bodies",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎"
        },
        {
            id: 2,
            qa_id: 102,
            question: "What is acceleration?",
            options: [],
            answer: "The rate of change of velocity",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎"
        },
        {
            id: 3,
            qa_id: 102,
            question: "What is Newton's first law?",
            options: [],
            answer: "An object in motion stays in motion unless acted upon by a force",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎"
        },
        {
            id: 4,
            qa_id: 102,
            question: "Define momentum.",
            options: [],
            answer: "The product of mass and velocity",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎"
        },
        {
            id: 5,
            qa_id: 102,
            question: "What is the unit of force?",
            options: [],
            answer: "Newton",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎"
        }
    ]
};


const QAEditWrapper: React.FC = () => {
    const { qa_id } = useParams();
    const qaData = mockQAData[qa_id ?? ""];

    if (!qaData) {
        return <p>指定されたクイズは見つかりません。</p>;
    }

    return <QAEdit data={qaData} />;
};

export default QAEditWrapper;
