// QADtail.tsx


import React from "react";
import { useParams } from "react-router-dom";
import "./QADetail.css";

const mockQAData: Record<string, any[]> = {
    "101": [
        {
            id: 1,
            qa_id: 101,
            question: "What is the capital of Japan?",
            options: ["Tokyo", "Osaka", "Kyoto"],
            answer: "Tokyo",
            mode: "multiple-choice",
            created_at: "2022-12-25",
            class: "地理",
            title: "日本の地理問題"
        }
    ],
    "102": [
        {
            id: 1,
            qa_id: 102,
            question: "What is gravity?",
            options: [],
            answer: "A force that attracts two bodies",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎"
        }
    ]
};

const mockHistoryData = [
    { id: 1, summary: "Tokyo answered correctly on 2022-12-26" },
    { id: 2, summary: "Osaka chosen incorrectly on 2022-12-27" },
    { id: 3, summary: "Kyoto chosen incorrectly on 2022-12-28" },
    { id: 4, summary: "Tokyo answered correctly on 2022-12-29" },
    { id: 5, summary: "Osaka chosen incorrectly on 2022-12-30" },
    { id: 6, summary: "Kyoto chosen incorrectly on 2023-01-01" },
    { id: 7, summary: "Tokyo answered correctly on 2023-01-02" }
];

const QADetail: React.FC = () => {
    const { qa_id } = useParams();
    const qaList = mockQAData[qa_id ?? ""];
    const firstItem = qaList && qaList.length > 0 ? qaList[0] : null;

    return (
        <div className="qadetail-result-container">
            <h1 className="qadetail-title">{firstItem.title}</h1>

            {firstItem && (
                <div className="qadetail-header-info">

                    <p className="qadetail-classname">授業名: {firstItem.class}</p>
                    <p className="qadetail-date">作成日: {firstItem.created_at}</p>
                </div>
            )}

            {qaList ? (
                <>
                    <ul className="qadetail-list">
                        {qaList.map((item, index) => (
                            <li key={index} className="qadetail-item">
                                <p className="qadetail-question">
                                    Q{index + 1}: {item.question}
                                </p>
                                {item.options && item.options.length > 0 && (
                                    <ul className="qadetail-options">
                                        {item.options.map((opt: string, i: number) => (
                                            <li key={i}>{opt}</li>
                                        ))}
                                    </ul>
                                )}
                                <p className="qadetail-answer">Answer: {item.answer}</p>
                            </li>
                        ))}
                    </ul>

                    <div className="qadetail-analysis">
                        <p>分析結果: 正解率 43%、誤答は「Osaka」「Kyoto」が多い傾向</p>
                    </div>

                    <p className="qadetail-history-title">Quiz history</p>
                    <div className="qadetail-history-container">
                        <ul className="qadetail-history-list">
                            {mockHistoryData.map((history) => (
                                <li key={history.id} className="qadetail-history-card">
                                    {history.summary}
                                </li>
                            ))}
                        </ul>
                    </div>
                </>
            ) : (
                <p>データが見つかりません。</p>
            )}
        </div>
    );
};

export default QADetail;
