// QADtail.tsx


import React from "react";
import { useParams } from "react-router-dom";
import "./QADetail.css";

import {
    BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
    LineChart, Line, ResponsiveContainer
} from "recharts";

const mockQAData: Record<string, any[]> = {
    "101": [
        {
            id: 1,
            qa_id: 101,
            question: "What is the capital of Japan?",
            options: ["Tokyo", "Osaka", "Kyoto"],
            answer: "Tokyo",
            mode: "multiple-choice",
            created_at: "2022-12-25",
            class: "地理",
            title: "日本の地理問題",
            satisfaction: 80 // ← 追加！
        }
    ],
    "102": [
        {
            id: 1,
            qa_id: 102,
            question: "What is gravity?",
            options: [],
            answer: "A force that attracts two bodies",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎",
            satisfaction: 80 // ← 追加！
        },
        {
            id: 2,
            qa_id: 102,
            question: "What is acceleration?",
            options: [],
            answer: "The rate of change of velocity",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎",
            satisfaction: 80 // ← 追加！
        },
        {
            id: 3,
            qa_id: 102,
            question: "State Newton's First Law of Motion.",
            options: [],
            answer: "An object will remain at rest or in uniform motion unless acted upon by a force.",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎",
            satisfaction: 80 // ← 追加！
        },
        {
            id: 4,
            qa_id: 102,
            question: "What is velocity?",
            options: [],
            answer: "Velocity is the speed of an object in a given direction.",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎",
            satisfaction: 80 // ← 追加！
        },
        {
            id: 5,
            qa_id: 102,
            question: "What is inertia?",
            options: [],
            answer: "Inertia is the tendency of an object to resist changes in its state of motion.",
            mode: "記述式問題",
            created_at: "2023-01-01",
            class: "物理基礎",
            title: "物理基礎",
            satisfaction: 80 // ← 追加！
        }
    ]

};



const optionStats = [
    { option: "Tokyo", count: 4 },
    { option: "Osaka", count: 2 },
    { option: "Kyoto", count: 2 },
];

const accuracyOverTime = [
    { date: "12/26", accuracy: 100 },
    { date: "12/27", accuracy: 0 },
    { date: "12/28", accuracy: 0 },
    { date: "12/29", accuracy: 100 },
    { date: "12/30", accuracy: 0 },
    { date: "1/1", accuracy: 0 },
    { date: "1/2", accuracy: 100 },
  ];

const mockHistoryData = [
    { id: 1, summary: "Tokyo answered correctly on 2022-12-26" },
    { id: 2, summary: "Osaka chosen incorrectly on 2022-12-27" },
    { id: 3, summary: "Kyoto chosen incorrectly on 2022-12-28" },
    { id: 4, summary: "Tokyo answered correctly on 2022-12-29" },
    { id: 5, summary: "Osaka chosen incorrectly on 2022-12-30" },
    { id: 6, summary: "Kyoto chosen incorrectly on 2023-01-01" },
    { id: 7, summary: "Tokyo answered correctly on 2023-01-02" }
];

const QADetail: React.FC = () => {
    const { qa_id } = useParams();
    const [isEditing, setIsEditing] = useState(false);
    const qaList = mockQAData[qa_id ?? ""];
    const firstItem = qaList && qaList.length > 0 ? qaList[0] : null;

    return (
        <div className="qadetail-result-container">
            <h1 className="qadetail-title">{firstItem.title}</h1>

            {firstItem && (
                <div className="qadetail-header-info">
                    <p className="qadetail-classname">授業名: {firstItem.class}</p>
                    <p className="qadetail-date">作成日: {firstItem.created_at}</p>
                </div>
            )}

            {qaList ? (
                <>
                    {/* <ul className="qadetail-list">
                        {qaList.map((item, index) => (
                            <li key={index} className="qadetail-item">
                                <p className="qadetail-question">
                                    Q{index + 1}: {item.question}
                                </p>
                                {item.options && item.options.length > 0 && (
                                    <ul className="qadetail-options">
                                        {item.options.map((opt: string, i: number) => (
                                            <li key={i}>{opt}</li>
                                        ))}
                                    </ul>
                                )}
                                <p className="qadetail-answer">Answer: {item.answer}</p>
                            </li>
                        ))}
                    </ul> */}

                    <div className="qadetail-analysis-graphs">
                        <h2 className="qadetail-analysis-title">分析結果</h2>

                        <div className="qadetail-charts-row">
                            {/* グラフ1 */}
                            <div className="qadetail-chart">
                                <h3>選択肢の選ばれた回数</h3>
                                <ResponsiveContainer width="100%" height={300}>
                                    <BarChart data={optionStats}>
                                        <CartesianGrid strokeDasharray="3 3" />
                                        <XAxis dataKey="option" />
                                        <YAxis />
                                        <Tooltip />
                                        <Legend />
                                        <Bar dataKey="count" fill="#007acc" />
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>

                            {/* グラフ2 */}
                            <div className="qadetail-chart">
                                <h3>正解率の推移</h3>
                                <ResponsiveContainer width="100%" height={300}>
                                    <LineChart data={accuracyOverTime}>
                                        <CartesianGrid strokeDasharray="3 3" />
                                        <XAxis dataKey="date" />
                                        <YAxis domain={[0, 100]} />
                                        <Tooltip />
                                        <Legend />
                                        <Line type="monotone" dataKey="accuracy" stroke="#ff6600" />
                                    </LineChart>
                                </ResponsiveContainer>
                            </div>
                        </div>
                    </div>


                    <div className="qadetail-analysis">
                        <p>分析結果: 正解率 43%、誤答は「Osaka」「Kyoto」が多い傾向</p>
                    </div>

                    <p className="qadetail-history-title">クイズ詳細</p>
                    <div className="qadetail-history-container">
                        <ul className="qadetail-history-list">
                            {qaList.map((item, index) => (
                                <li key={index} className="qadetail-history-card">
                                    <p><strong>問題：</strong> {item.question}</p>

                                    {item.options && item.options.length > 0 && (
                                        <ul style={{ paddingLeft: "1rem", margin: "0.5rem 0" }}>
                                            {item.options.map((opt: string, i: number) => (
                                                <li key={i}>{opt}</li>
                                            ))}
                                        </ul>
                                    )}

                                    <p><strong>答え：</strong> {item.answer}</p>
                                    <p><strong>満足度</strong></p>
                                    <div className="qadetail-satisfaction-bar">
                                        <div
                                            className="qadetail-satisfaction-fill"
                                            style={{ width: `${item.satisfaction}%` }}
                                        />
                                    </div>
                                </li>
                            ))}
                        </ul>
                    </div>
                    <Link to={`/qa/${qa_id}/edit`} className="edit-link">編集する</Link>
                </>
            ) : (
                <p>データが見つかりません。</p>
            )}
        </div>
    );
};

export default QADetail;
