import React, { useCallback, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import './FileUploader.css';

const FileUploader = () => {
    const [questionCount, setQuestionCount] = useState(5); // 質問数
    const [mode, setMode] = useState("３択問題"); // モード

    const onDrop = useCallback((acceptedFiles: File[]) => {
        const pdfFile = acceptedFiles[0];

        if (pdfFile && pdfFile.type === "application/pdf") {
            const formData = new FormData();
            formData.append("file", pdfFile);
            formData.append("questionCount", String(questionCount));
            formData.append("mode", mode);

            fetch("http://localhost:3001/upload", {
                method: "POST",
                body: formData,
            })
                .then(res => res.json())
                .then(data => console.log("成功:", data))
                .catch(err => console.error("エラー:", err));
        } else {
            alert("PDFファイルのみアップロード可能です");
        }
    }, [questionCount, mode]);

    const { getRootProps, getInputProps, isDragActive } = useDropzone({
        onDrop,
        accept: { 'application/pdf': [] }
    });

    return (
        <div>
            <div className="uploader-container" {...getRootProps()}>
                <input {...getInputProps()} />
                {
                    isDragActive
                        ? <p>ここにPDFをドロップしてください...</p>
                        : <p>ここをクリックまたはドラッグしてPDFファイルをアップロード</p>
                }
            </div>

            {/* 質問数選択 */}
            <div className="selector">
                <label>生成する質問数:</label>
                <div className="button-group">
                    {[5, 10, 15, 20].map((count) => (
                        <button
                            key={count}
                            className={questionCount === count ? "selected" : ""}
                            onClick={() => setQuestionCount(count)}
                        >
                            {count}
                        </button>
                    ))}
                </div>
            </div>

            {/* モード選択 */}
            <div className="selector">
                <label>出題モード:</label>
                <div className="button-group">
                    {["３択問題", "４択問題", "４択複数選択問題", "記述式問題"].map((m) => (
                        <button
                            key={m}
                            className={mode === m ? "selected" : ""}
                            onClick={() => setMode(m)}
                        >
                            {m}
                        </button>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default FileUploader;
