import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "./QADetail.css";

import {
    PieChart, Pie, Cell,
    BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend,
    LineChart, Line, ResponsiveContainer, ReferenceLine
} from "recharts";


interface QAItem {
    qa_id: number;
    question: string;
    options: string[];
    answer: string;
}

interface QAInfo {
    id: number;
    created_at: string;
    className: string;
    title: string;
    mode: string;
    questionCount: number;
}

interface AnalysisData {
    summary: {
        total_answers: number;
        total_correct: number;
        overall_accuracy: number;
    };
    score_distribution: Record<string, number>;
    per_quiz_analysis: {
        qa_id: number;
        correct_rate: number | null;
        option_distribution: Record<string, number>;
        total_answers: number;
        satisfaction_summary?: {
            good: number;
            neutral: number;
            bad: number;
        };
    }[];
  }

const QADetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [qaItems, setQaItems] = useState<QAItem[]>([]);
    const [qaInfo, setQaInfo] = useState<QAInfo | null>(null);
    const [analysis, setAnalysis] = useState<AnalysisData | null>(null);

    useEffect(() => {
        if (id) {
            Promise.all([
                axios.get<QAItem[]>(`http://localhost:8000/qaitem/${id}`),
                axios.get<QAInfo>(`http://localhost:8000/qainfo/${id}`),
                axios.get<AnalysisData>(`http://localhost:8000/qaanalysis/${id}`),
            ])
                .then(([itemsRes, infoRes, analysisRes]) => {
                    setQaItems(itemsRes.data);
                    setQaInfo(infoRes.data);
                    setAnalysis(analysisRes.data);
                })
                .catch(err => {
                    console.error("データ取得失敗:", err);
                });
        }
    }, [id]);

    return (
        <div className="qadetail-result-container">
            <h1 className="qadetail-title">{qaInfo?.title ?? "読み込み中..."}</h1>

            {qaInfo && (
                <div className="qadetail-header-info">
                    <p className="qadetail-classname">授業名: {qaInfo.className}</p>
                    <p className="qadetail-date">
                        作成日: {
                            qaInfo.created_at.length >= 12
                                ? `${qaInfo.created_at.slice(0, 4)}.${qaInfo.created_at.slice(4, 6)}.${qaInfo.created_at.slice(6, 8)} ${qaInfo.created_at.slice(8, 10)}:${qaInfo.created_at.slice(10, 12)}`
                                : qaInfo.created_at
                        }
                    </p>
                </div>
            )}

            {qaItems.length > 0 && analysis ? (
                <>
                    <div className="qadetail-analysis-graphs">
                        <h2 className="qadetail-analysis-title">分析結果</h2>



                        <div className="qadetail-charts-row">
                            {/* 正解率の一覧 */}
                            <div className="qadetail-chart">
                                <h3>各問題の正解率</h3>
                                <ResponsiveContainer width="100%" height={300}>
                                    <BarChart data={analysis.per_quiz_analysis.map(q => ({
                                        qa_id: `Q${q.qa_id + 1}`,
                                        accuracy: q.correct_rate !== null ? Math.round(q.correct_rate * 100) : 0,
                                    }))}>
                                        <CartesianGrid strokeDasharray="3 3" />
                                        <XAxis dataKey="qa_id" />
                                        <YAxis domain={[0, 100]} />
                                        <Tooltip />
                                        <Legend />
                                        <Bar dataKey="accuracy" fill="#28a745" />
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>

                            {/* 得点分布 */}
                            <div className="qadetail-chart">
                                <h3>点数分布（人数）</h3>
                                <ResponsiveContainer width="100%" height={300}>
                                    <BarChart
                                        data={Array.from({ length: qaInfo?.questionCount! + 1 }, (_, i) => ({
                                            score: i.toString(),
                                            count: analysis.score_distribution[i.toString()] ?? 0
                                        }))}
                                    >
                                        <CartesianGrid strokeDasharray="3 3" />
                                        <XAxis dataKey="score" />
                                        <YAxis allowDecimals={false} />
                                        <Tooltip />
                                        <Legend />
                                        <Bar dataKey="count" fill="#6f42c1" />
                                    </BarChart>
                                </ResponsiveContainer>
                            </div>

                        </div>
                    </div>

                    <div className="qadetail-analysis">
                        <p>
                            分析結果: 正解率 {Math.round(analysis.summary.overall_accuracy * 100)}%、回答数 {analysis.summary.total_answers} 件、正解数 {analysis.summary.total_correct} 件
                        </p>
                    </div>

                    <p className="qadetail-history-title">クイズ詳細</p>
                    <div className="qadetail-history-container">

                        <ul className="qadetail-history-list">
                            {qaItems.map((item, index) => {
                                const analysisForItem = analysis?.per_quiz_analysis.find(a => a.qa_id === item.qa_id);
                                const optionDist = analysisForItem?.option_distribution ?? {};
                                const satisf = analysisForItem?.satisfaction_summary;

                                console.log(`満足度データ for Q${item.qa_id + 1}:`, {
                                    good: satisf?.good,
                                    neutral: satisf?.neutral,
                                    bad: satisf?.bad
                                });


                                const satisfPieData = satisf
                                    ? [
                                        { name: "Good", value: satisf.good },
                                        { name: "Neutral", value: satisf.neutral },
                                        { name: "Bad", value: satisf.bad },
                                    ]
                                    : [];

                                const COLORS = ["#28a745", "#ffc107", "#dc3545"];

                                return (
                                    <li key={index} className="qadetail-history-card">
                                        <p><strong>問題：</strong> {item.question}</p>

                                        {/* 選択肢のリスト */}
                                        <ul>
                                            {item.options.map((opt: string, i: number) => {
                                                const normalizedOption = opt.trim().replace(/\s+/g, '');
                                                const normalizedAnswer = item.answer.trim().replace(/\s+/g, '');
                                                const isCorrect = normalizedOption === normalizedAnswer;

                                                const rawRate = optionDist[opt.trim()] ?? 0;
                                                const percentage = Math.round(rawRate * 100);

                                                return (
                                                    <li
                                                        key={i}
                                                        style={isCorrect ? { color: "green", fontWeight: "bold" } : {}}
                                                    >
                                                        {opt.trim()}（{percentage}%）
                                                    </li>
                                                );
                                            })}
                                        </ul>

                                        <p><strong>答え：</strong> {item.answer}</p>

                                        {/* 満足度グラフ（存在する場合のみ） */}
                                        // 満足度グラフ（存在する場合のみ）
                                        {satisf && (satisf.good > 0 || satisf.bad > 0) && (
                                            <div style={{ width: 500, height: 200, margin: '2rem auto' }}>
                                                <h4 style={{ textAlign: 'center' }}>満足度</h4>
                                                <ResponsiveContainer width="100%" height="100%">
                                                    <BarChart
                                                        layout="vertical"
                                                        data={[{
                                                            name: "満足度",
                                                            Good: satisf.good,
                                                            Bad: satisf.bad,
                                                        }]}
                                                        margin={{ top: 20, right: 20, left: 20, bottom: 20 }}
                                                    >
                                                        <CartesianGrid strokeDasharray="3 3" />
                                                        <XAxis
                                                            type="number"
                                                            domain={[
                                                                -Math.max(satisf.good, satisf.bad) - 1,
                                                                Math.max(satisf.good, satisf.bad) + 1
                                                            ]}
                                                            tickFormatter={(tick) => Math.abs(tick).toString()}
                                                            />
                                                        <YAxis dataKey="name" type="category" hide />
                                                        <Tooltip
                                                            formatter={(value: number) => `${Math.abs(value)} 件`}
                                                        />
                                                        <Legend
                                                            verticalAlign="top"
                                                            height={30}
                                                            wrapperStyle={{ fontSize: "12px" }}
                                                        />
                                                        <ReferenceLine x={0} stroke="#000" />
                                                        <Bar dataKey="Good" fill="#28a745" stackId="a" />
                                                        <Bar dataKey="Bad" fill="#dc3545" stackId="a" />
                                                    </BarChart>
                                                </ResponsiveContainer>
                                            </div>
                                        )}




                                    )
                                    </li>
                                );
                            })}
                        </ul>

                    </div>



                    <div className="qaedit-save-button">
                        <Link to={`/qa/${id}/edit`} className="qaedit-button">編集する</Link>
                    </div>
                </>
            ) : (
                <p>データが見つかりません。</p>
            )}
        </div>
    );
};

export default QADetail;
