import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";

interface QAItem {
    id: number;
    qa_id: number;
    question: string;
    options: string[];
    answer: string;
    mode: string;
    created_at: string;
    class: string;
    title: string;
}

interface QAEditProps {
    data: Record<string, QAItem[]>;
}

const QAEdit: React.FC<QAEditProps> = ({ data }) => {
    const { qa_id } = useParams();
    const navigate = useNavigate();

    const qaList = data[qa_id ?? ""] || [];
    const firstItem = qaList[0];

    const [title, setTitle] = useState(firstItem?.title || "");
    const [className, setClassName] = useState(firstItem?.class || "");
    const [question, setQuestion] = useState(firstItem?.question || "");
    const [answer, setAnswer] = useState(firstItem?.answer || "");
    const [options, setOptions] = useState(firstItem?.options || []);

    const handleSave = () => {
        console.log("保存データ：", {
            title,
            className,
            question,
            answer,
            options,
        });
        navigate(`/qa/${qa_id}`);
    };

    return (
        <div style={{ padding: "1rem" }}>
            <h2>クイズ編集</h2>

            <label>タイトル</label>
            <input value={title} onChange={(e) => setTitle(e.target.value)} />

            <label>授業名</label>
            <input value={className} onChange={(e) => setClassName(e.target.value)} />

            <label>問題</label>
            <textarea value={question} onChange={(e) => setQuestion(e.target.value)} />

            <label>答え</label>
            <input value={answer} onChange={(e) => setAnswer(e.target.value)} />

            <label>選択肢（カンマ区切り）</label>
            <input
                value={options.join(", ")}
                onChange={(e) =>
                    setOptions(e.target.value.split(",").map((opt) => opt.trim()))
                }
            />

            <br />
            <button onClick={handleSave}>保存して戻る</button>
        </div>
    );
};

export default QAEdit;
