import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./QADetail.css"; // QADetail.css を共通で使用

interface QAItem {
    id: number;
    qa_id: number;
    question: string;
    options: string[];
    answer: string;
    mode: string;
    created_at: string;
    class: string;
    title: string;
}

interface QAEditProps {
    data: QAItem[];
}

const QAEdit: React.FC<QAEditProps> = ({ data }) => {
    const { qa_id } = useParams();
    const navigate = useNavigate();

    const qaList = data || [];
    const firstItem = qaList[0];

    const [title, setTitle] = useState(firstItem?.title || "");
    const [className, setClassName] = useState(firstItem?.class || "");
    const [questions, setQuestions] = useState(
        qaList.map((item) => ({
            question: item.question,
            answer: item.answer,
            options: item.options,
        }))
    );

    const handleQuestionChange = (
        index: number,
        key: keyof typeof questions[0],
        value: string
    ) => {
        const updated = [...questions];
        if (key === "options") {
            updated[index][key] = value.split(",").map((v) => v.trim());
        } else {
            (updated[index] as any)[key] = value;
        }
        setQuestions(updated);
    };

    const handleSave = () => {
        console.log("保存データ：", {
            qa_id,
            title,
            className,
            questions,
        });
        navigate(`/qa/${qa_id}`);
    };

    return (
        <div className="qadetail-result-container">
            <input
                type="text"
                className="qadetail-title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
                placeholder="タイトルを入力"
            />
            <div className="qadetail-header-info">
                <div className="qadetail-classname-box">
                    <label className="qadetail-label">授業名</label>
                    <input
                        className="qadetail-classname"
                        value={className}
                        onChange={(e) => setClassName(e.target.value)}
                    />
                </div>
                <p className="qadetail-date">作成日: {firstItem.created_at}</p>
            </div>

            <ul className="qadetail-list">
                {questions.map((item, index) => (
                    <li key={index} className="qadetail-item">
                        <div style={{ marginBottom: "1rem" }}>
                            <p className="qadetail-question">Q{index + 1}</p>
                            <label>問題</label>
                            <textarea
                                className="qadetail-edit-textarea"
                                value={item.question}
                                onChange={(e) => handleQuestionChange(index, "question", e.target.value)}
                            />
                        </div>
                        <div style={{ marginBottom: "1rem" }}>
                            <label>選択肢（カンマ区切り）</label>
                            <input
                                className="qadetail-edit-input"
                                value={item.options.join(", ")}
                                onChange={(e) => handleQuestionChange(index, "options", e.target.value)}
                            />
                        </div>
                        <div style={{ marginBottom: "1rem" }}>
                            <label>答え</label>
                            <input
                                className="qadetail-edit-input"
                                value={item.answer}
                                onChange={(e) => handleQuestionChange(index, "answer", e.target.value)}
                            />
                        </div>
                    </li>
                ))}
            </ul>

            <div style={{ textAlign: "center", marginTop: "2rem" }}>
                <button onClick={handleSave}>保存して戻る</button>
            </div>
        </div>
    );
};

export default QAEdit;
