// QALiat.tsx


import React from "react";
import { Link } from "react-router-dom";
import "./QAList.css";

const mockQAList = [
    { id: 101, title: "日本の地理問題" },
    { id: 102, title: "物理基礎" },
    { id: 102, title: "Title" },
    { id: 102, title: "Title" },
    { id: 102, title: "Title" },
    { qa_id: 102, title: "Title" },
    { qa_id: 102, title: "Title" },
    { qa_id: 102, title: "Title" },
];

const QAList: React.FC = () => {
    return (
        <div className="qa-container">
            <h1 className="qa-title">QA一覧</h1>
            <p className="qa-description">以下は登録されているQA項目の一覧です。カードをクリックすると詳細ページが表示されます。</p>
            <div className="qa-card-container">
                {mockQAList.map((qa) => (
                    <Link
                        to={`/qa/${qa.id}`}
                        key={qa.id}
                        className="qa-card-link"
                        style={{ textDecoration: 'none', color: 'inherit' }}  // 下線消し、色引き継ぎ
                    >
                        <div className="qa-card">
                            <h2 className="qa-card-title">{qa.title}</h2>
                        </div>
                    </Link>
                ))}
            </div>
        </div>
    );
};

export default QAList;
