import React, { useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./QAEdit.css"; // 新たに編集用CSSファイルを使用

interface QAItem {
    id: number;
    qa_id: number;
    question: string;
    options: string[];
    answer: string;
    mode: string;
    created_at: string;
    class: string;
    title: string;
}

interface QAEditProps {
    data: QAItem[];
}

const QAEdit: React.FC<QAEditProps> = ({ data }) => {
    const { qa_id } = useParams();
    const navigate = useNavigate();

    const qaList = data || [];
    const firstItem = qaList[0];

    const [title, setTitle] = useState(firstItem?.title || "");
    const [className, setClassName] = useState(firstItem?.class || "");
    const [questions, setQuestions] = useState(
        qaList.map((item) => ({
            question: item.question,
            answer: item.answer,
            options: item.options,
        }))
    );

    const handleQuestionChange = (
        index: number,
        key: keyof typeof questions[0],
        value: string
    ) => {
        const updated = [...questions];
        if (key === "options") {
            updated[index][key] = value.split(",").map((v) => v.trim());
        } else {
            (updated[index] as any)[key] = value;
        }
        setQuestions(updated);
    };

    const handleSave = () => {
        console.log("保存データ：", {
            qa_id,
            title,
            className,
            questions,
        });
        navigate(`/qa/${qa_id}`);
    };

    return (
        <div className="qaedit-container">
            <h1 className="qaedit-title-input">
                <input
                    className="qaedit-input-title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                />
            </h1>
            <div className="qaedit-header">
                <div className="qaedit-classname-box">
                    <label className="qaedit-label">授業名</label>
                    <input
                        className="qaedit-input"
                        value={className}
                        onChange={(e) => setClassName(e.target.value)}
                    />
                </div>
                <p className="qaedit-date">作成日: {firstItem.created_at}</p>
            </div>

            <ul className="qaedit-list">
                {questions.map((item, index) => (
                    <li key={index} className="qaedit-item">
                        <p className="qaedit-question">Q{index + 1}</p>
                        <label>問題</label>
                        <textarea
                            className="qaedit-textarea"
                            value={item.question}
                            onChange={(e) => handleQuestionChange(index, "question", e.target.value)}
                        />
                        <label>選択肢（カンマ区切り）</label>
                        <input
                            className="qaedit-input"
                            value={item.options.join(", ")}
                            onChange={(e) => handleQuestionChange(index, "options", e.target.value)}
                        />
                        <label>答え</label>
                        <input
                            className="qaedit-input"
                            value={item.answer}
                            onChange={(e) => handleQuestionChange(index, "answer", e.target.value)}
                        />
                    </li>
                ))}
            </ul>

            <div className="qaedit-save-button">
                <button onClick={handleSave}>保存して戻る</button>
            </div>
        </div>
    );
};

export default QAEdit;
